mapper.select:
select

from DON_USER_INFO where USER_NAME = #{userName,jdbcType=VARCHAR}
  or NICKNAME= #{nickname,jdbcType=VARCHAR}
  or USER_FOREIGN_NAME = #{userForeignName,jdbcType=VARCHAR}

select USER_ID userId, USER_NAME userName, NICKNAME, USER_FOREIGN_NAME userForeignName, SEX, BIRTHDAY, PHONE_NO phoneNo, EMAIL, ADDRESS, AVATAR, USER_STATUS userStatus, REMARK from DON_USER_INFO where 1=1
and USER_NAME = #{userName,jdbcType=VARCHAR}
and NICKNAME= #{nickname,jdbcType=VARCHAR}
and EMAIL = #{email,jdbcType=VARCHAR}
